import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import ListGroup from "react-bootstrap/ListGroup";
import {Link} from "react-router-dom";
import {useState} from "react";

const cardStyle = {
    margin: "8px auto",
    width: '300px',
    textColor: "black",
    textAlign: "center"
};

const cardImage = {
    width: "100%",
    height: "200px",
};

const cardButton = {
    width: "100%"
};

const moreLink = {
    textDecoration: 'none',
    color: "black"
};

const TruckCard = (props) => {
    const [isSelected, toggleSelect] = useState(false)

    return (
            <Card key={props.id} style={cardStyle}>
                <Link to={`/rent/${props.id}`} style={moreLink}>
                    <Card.Img style={cardImage} className="img-fluid" variant="top" src={props.image} height={100} width={100}/>
                <Card.Body>
                    <Card.Title>{props.brand + " " + props.name}</Card.Title>
                </Card.Body>
                </Link>
                <ListGroup className="list-group-flush">
                    <ListGroup.Item>Объём: {props.capacity}м<sup>3</sup></ListGroup.Item>
                    <ListGroup.Item>Стоимость: {props.price}р.</ListGroup.Item>

                    <Link to={`/rent/${props.id}`}>
                        <Button variant="primary" style={cardButton} onClick={props.onClick(isSelected)}>Выбрать</Button>
                    </Link>
                </ListGroup>
            </Card>
    );
}

export default TruckCard;
