import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import ListGroup from "react-bootstrap/ListGroup";

const cardStyle = {
    margin: "8px auto",
    width: '300px',
    height: '200px',
    textColor: "black",
    textAlign: "center"
};

const TruckCard = ({name, capacity, price, image}) => {
    return (
            <Card style={cardStyle}>
                <Card.Img  className="img fluid" variant="top" src={image} height={100} width={100}/>
                <Card.Body>
                    <Card.Title>{name}</Card.Title>
                </Card.Body>
                <ListGroup className="list-group-flush">
                    <ListGroup.Item>Объём: {capacity}м<sup>3</sup></ListGroup.Item>
                    <ListGroup.Item>Стоимость: {price}р.</ListGroup.Item>

                    <Button variant="primary" style={{alignContent: "center"}}>Выбрать</Button>
                </ListGroup>
            </Card>
    );
}

export default TruckCard;
