import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import ListGroup from "react-bootstrap/ListGroup";
import {Link} from "react-router-dom";

const cardStyle = {
    margin: "8px auto",
    width: '300px',
    textColor: "black",
    textAlign: "center"
};

const cardImage = {
    width: "100%",
    height: "200px",
};

const cardButton = {
    width: "100%"
};

const TruckCard = ({id, name, capacity, price, image}) => {
    return (
            <Card key={id} style={cardStyle}>
                <Card.Img style={cardImage} className="img-fluid" variant="top" src={image} height={100} width={100}/>
                <Card.Body>
                    <Card.Title>{name}</Card.Title>
                </Card.Body>
                <ListGroup className="list-group-flush">
                    <ListGroup.Item>Объём: {capacity}м<sup>3</sup></ListGroup.Item>
                    <ListGroup.Item>Стоимость: {price}р.</ListGroup.Item>
                    <Link to={`/rent/${id}`}>
                        <Button variant="primary" style={cardButton}>Выбрать</Button>
                    </Link>
                </ListGroup>
            </Card>
    );
}

export default TruckCard;
