import Card from "react-bootstrap/Card";
import ButtonGroup from "react-bootstrap/ButtonGroup";
import ListGroup from "react-bootstrap/ListGroup";
import ToggleButton from "react-bootstrap/ToggleButton";
import { useState } from 'react';

const cardStyle = {
    margin: "8px",
    width: '18rem',
    textColor: "black",
    textAlign: "center"
};

function TruckCard() {
    const [checked, setChecked] = useState(false);
    return (
        <div>
            <Card style={cardStyle}>
                <Card.Img variant="top" src="https://s1.1zoom.ru/big0/187/302174-Berserker.jpg" />
                <Card.Body>
                    <Card.Title>Freightliner Truck</Card.Title>
                </Card.Body>
                <ListGroup className="list-group-flush">
                    <ListGroup.Item>Полезный объём: 16м<sup>3</sup></ListGroup.Item>
                    <ListGroup.Item>Стоимость: 10000 руб./ч.</ListGroup.Item>
                </ListGroup>
                <ButtonGroup className="mb-2">
                    <ToggleButton
                        id="toggle-check"
                        type="checkbox"
                        variant="secondary"
                        checked={checked}
                        value="1"
                        onChange={(e) => setChecked(e.currentTarget.checked)}
                    >
                        Checked
                    </ToggleButton>
                </ButtonGroup>
            </Card>
        </div>
    );
}

export default TruckCard;
