import React, {useEffect, useState} from 'react';
import {Card, Col, Row, Button, Spinner} from "react-bootstrap";
import TruckCard from "../Components/TruckCard";
import IntervalSelector from "../Components/IntervalSelector";
import getData from "../Components/data";
import ListGroup from "react-bootstrap/ListGroup";

const TableBlockStyle = {
    margin: "20px 15%",
    textAlign: 'center',
}

const TableCardStyle = {
    display: "inline-block",
    margin: "8px 8px 8px 0px",
}

function DetailedTruck(id, name, capacity, price, image, country, description, payload) {
    return (
        <>
            <Card key={name} style={cardStyle}>
                <Card.Img style={cardImage} className="img-fluid" variant="top" src={image} height={100} width={100}/>
                <Card.Body>
                    <Card.Title>{name}</Card.Title>
                </Card.Body>
                <ListGroup className="list-group-flush">
                    <ListGroup.Item>Объём: {capacity}м<sup>3</sup></ListGroup.Item>
                    <ListGroup.Item>Стоимость: {price}р.</ListGroup.Item>
                    <ListGroup.Item>Страна: {country}р.</ListGroup.Item>
                    <ListGroup.Item>Описание: {description}р.</ListGroup.Item>
                    <ListGroup.Item>Грузоподъемность: {payload}р.</ListGroup.Item>

                    <Button href={`/rent/${name}`} variant="primary" style={{alignContent: "center"}}>Выбрать</Button>
                </ListGroup>
            </Card>
        </>
    );
}

export default DetailedTruck;
